import 'package:flutter/material.dart';

void main() {
  runApp(const PlayerApp());
}

class PlayerApp extends StatefulWidget {
  const PlayerApp({super.key});

  @override
  State<PlayerApp> createState() => _PlayerAppState();
}

class _PlayerAppState extends State<PlayerApp> {
  int _counter = 0;
  List<String> playerlist = <String>[
    "https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcQ7stGLtmEKOsAyJ3v5j6e5VSaVGALuXO1PR7h0-b9lH5tcSOb4",
    "data:image/jpeg;base64,"
    "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9b/Virat_Kohli_in_PMO_New_Delhi.jpg/330px-Virat_Kohli_in_PMO_New_Delhi.jpg"
        "https://media.gettyimages.com/id/90968090/photo/sandton-south-africa-yuvraj-singh-poses-during-the-icc-champions-photocall-session-of-the.jpg?s=612x612&w=0&k=20&c=bUawaKpfQP5cJvaR8SHo_ekSUCKkYdbW1n1cTJqec0w=",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRlBzUzSvez67KE_QlHYI-vj7c_YyMh4_74NU2c9Ts046g-x-25yD8ODRqFceH5OfCzDmQ&usqp=CAU",
  ];
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: "Players",
      debugShowCheckedModeBanner: false,
      home: Scaffold(
        appBar: AppBar(
          title: const Text("Player App "),
          centerTitle: true,
          backgroundColor: Colors.pink,
        ),
        body: Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: <Widget>[
              Image.network(
                playerlist[_counter],
                height: 300,
              ),
            ],
          ),
        ),
        floatingActionButton: FloatingActionButton(
          onPressed: () {
            if (_counter < playerlist.length - 1) {
              _counter++;
            } else {
              _counter = 0;
            }
            setState(() {});
          },
          tooltip: 'Increment',
          child: const Icon(Icons.add),
        ),
      ),
    );
  }
}
